local qq = eqqlocal Plugin = {	Name = "Bunny Hop",	Alias = "bhop"}Plugin.Init = function()	qq.CreateSetting(qq.MENU_GENERIC, Plugin, "enabled", "Bunnyhop", true, {Save = true})endlocal IN_JUMP = qq.GlobalCopy.IN_JUMP--@Wizard, localizinglocal MOVETYPE_WALK = qq.GlobalCopy.MOVETYPE_WALKlocal ValidEntity = ValidEntitylocal LocalPlayer = LocalPlayerlocal lp = LocalPlayer()local cmdMetaGetButtons = qq.Meta.Cmd.GetButtonslocal cmdMetaSetButtons = qq.Meta.Cmd.SetButtonslocal entMetaWaterLevel = qq.Meta.Ent.WaterLevellocal entMetaGetMoveType = qq.Meta.Ent.GetMoveTypelocal entMetaOnGround = qq.Meta.Ent.OnGroundlocal buttonsPlugin.Bhop = function(ucmd)	if not qq.Setting(Plugin, "enabled") then return end	if not ValidEntity(lp) then		lp = LocalPlayer()	end	buttons = cmdMetaGetButtons(ucmd)	if (buttons & IN_JUMP) == IN_JUMP and not (entMetaWaterLevel(lp) > 0) and (entMetaGetMoveType(lp) == MOVETYPE_WALK) then--@Wizard, removed some locals...		if entMetaOnGround(lp) then			cmdMetaSetButtons(ucmd, buttons | IN_JUMP)		else			cmdMetaSetButtons(ucmd, buttons - IN_JUMP)		end	endendPlugin.Hooks = {	PreCreateMove = Plugin.Bhop}qq.RegisterPlugin(Plugin)