local qq = eqqlocal Plugin = {	Name = "Velocity Fix (Speedhake)",	Alias = "velfix",	Time = CurTime()}local EntMeta = qq.Meta.Entlocal entMetaGetPos = qq.Meta.Ent.GetPoslocal CurTime = qq.GlobalCopy.CurTime--@Wizard, localizinglocal ValidEntity = qq.GlobalCopy.ValidEntitylocal type = qq.GlobalCopy.typePlugin.Init = function()	qq.CreateSetting(qq.MENU_GENERIC, Plugin, "enabled", "Enabled", true)endPlugin.ThinkEnd = function()	Plugin.Time = CurTime()endPlugin.ThinkStart = function()	Plugin.Time = CurTime() - Plugin.TimeendPlugin.LastPos = {}Plugin.Vels = {}Plugin.ThinkPlayer = function(pl)--@Wizard, removal of locals.	Plugin.Vels[pl] = (entMetaGetPos(pl) - (Plugin.LastPos[pl] or entMetaGetPos(pl))) / Plugin.Time	Plugin.LastPos[pl] = entMetaGetPos(pl)endEntMeta._GetVelocity = EntMeta._GetVelocity or EntMeta.GetVelocityEntMeta.GetVelocity = function(e)	if ValidEntity(e) and type(e) == "Player" and qq.Setting(Plugin, "enabled") then		return Plugin.Vels[e] or EntMeta._GetVelocity(e)	end	return EntMeta._GetVelocity(e)endPlugin.Hooks = {	ThinkStart = Plugin.ThinkStart,	ThinkPlayer = Plugin.ThinkPlayer,	ThinkEnd = Plugin.ThinkEnd}qq.RegisterPlugin(Plugin)